<?php
ini_set('display_errors', 1);
ini_set('display_startup_errors', 1);
error_reporting(E_ALL);
# Initialize the session
session_start();
require_once "./config.php";

# If user is not logged in then redirect him to login page
if (!isset($_SESSION["loggedin"]) || $_SESSION["loggedin"] !== TRUE) {
    echo "<script>" . "window.location.href='./login.php';" . "</script>";
    exit;
}
$balance = $_SESSION["balance"] ?? 0;
$user_id = $_SESSION["id"];

// Fetch user balance
$sql = "SELECT balance FROM users WHERE id = ?";
if ($stmt = $link->prepare($sql)) {
    $stmt->bind_param("i", $user_id);
    $stmt->execute();
    $stmt->bind_result($balance);
    $stmt->fetch();
    $stmt->close();
}

$totalDeposits = $totalWithdrawals = $submitted = $pending = $rejected = 0.00;
$totalSubmittedDeposits = $totalPendingDeposits = $totalRejectedDeposits = 0.00;
$totalSubmittedWithdrawals = $totalPendingWithdrawals = $totalRejectedWithdrawals = 0.00;

// Fetch deposit details grouped by status for the specific user
$queryDeposits = "SELECT status, SUM(amount) AS total FROM deposits WHERE user_id = ? GROUP BY status";
if ($stmt = $link->prepare($queryDeposits)) {
    $stmt->bind_param("i", $user_id);
    $stmt->execute();
    $resultDeposits = $stmt->get_result();

    while ($row = $resultDeposits->fetch_assoc()) {
        switch ($row['status']) {
            case 'confirmed':
                $totalSubmittedDeposits = $row['total'];
                break;
            case 'pending':
                $totalPendingDeposits = $row['total'];
                break;
            case 'rejected':
                $totalRejectedDeposits = $row['total'];
                break;
        }
        $totalDeposits += $row['total'];
    }
    $stmt->close();
} else {
    echo "Error fetching deposits: " . mysqli_error($link);
}

// Fetch withdrawal details grouped by status for the specific user
$queryWithdrawals = "SELECT status, SUM(amount) AS total FROM withdrawals WHERE user_id = ? GROUP BY status";
if ($stmt = $link->prepare($queryWithdrawals)) {
    $stmt->bind_param("i", $user_id);
    $stmt->execute();
    $resultWithdrawals = $stmt->get_result();

    while ($row = $resultWithdrawals->fetch_assoc()) {
        switch ($row['status']) {
            case 'processed':
                $totalSubmittedWithdrawals = $row['total'];
                break;
            case 'pending':
                $totalPendingWithdrawals = $row['total'];
                break;
            case 'rejected':
                $totalRejectedWithdrawals = $row['total'];
                break;
        }
        $totalWithdrawals += $row['total'];
    }
    $stmt->close();
} else {
    echo "Error fetching withdrawals: " . mysqli_error($link);
}

// Fetch cryptocurrency-specific deposits and withdrawals for the specific user
$queryCryptoBalances = "
    SELECT gateway,
           COALESCE(SUM(CASE WHEN transaction_type = 'deposit' THEN amount END), 0) AS total_deposited,
           COALESCE(SUM(CASE WHEN transaction_type = 'withdrawal' THEN amount END), 0) AS total_withdrawn,
           GREATEST(
               COALESCE(SUM(CASE WHEN transaction_type = 'deposit' THEN amount END), 0) 
               - COALESCE(SUM(CASE WHEN transaction_type = 'withdrawal' THEN amount END), 0), 
               0
           ) AS balance,
           COUNT(CASE WHEN transaction_type = 'deposit' THEN 1 END) AS total_deposit_transactions
    FROM (
        SELECT gateway, amount, 'deposit' AS transaction_type
        FROM deposits
        WHERE user_id = ? AND status = 'confirmed'

        UNION ALL

        SELECT gateway, amount, 'withdrawal' AS transaction_type
        FROM withdrawals
        WHERE user_id = ? AND status = 'processed'
    ) AS combined_transactions
    GROUP BY gateway
";

if ($stmt = $link->prepare($queryCryptoBalances)) {
    $stmt->bind_param("ii", $user_id, $user_id);
    $stmt->execute();
    $resultCryptoBalances = $stmt->get_result();

    $cryptoBalances = [];
    while ($row = $resultCryptoBalances->fetch_assoc()) {
        $cryptoBalances[] = [
            'gateway' => $row['gateway'],
            'total_deposited' => $row['total_deposited'],
            'total_withdrawn' => $row['total_withdrawn'],
            'total_deposit_transactions' => $row['total_deposit_transactions'],
            'balance' => $row['balance']
        ];
    }
    $stmt->close();
} else {
    echo "Error fetching cryptocurrency balances: " . mysqli_error($link);
}

mysqli_close($link);

?>




