<script src="https://cdn.jsdelivr.net/npm/apexcharts"></script>
<script>
    // Initialize chart options
    var options = {
        chart: {
            height: 350,
            type: "line",
            toolbar: { show: false },
            animations: {
                enabled: true,
                easing: 'linear',
                dynamicAnimation: { speed: 1000 }
            },
        },
        series: [{
            name: "BTC Price (USD)",
            data: []
        }],
        xaxis: {
            type: 'datetime',
            title: { text: "Time" }
        },
        yaxis: {
            title: { text: "Price (USD)" },
            labels: { formatter: (val) => `$${val.toFixed(2)}` }
        },
        fill: {
            type: "gradient",
            gradient: {
                shadeIntensity: 1,
                opacityFrom: 0.5,
                opacityTo: 0.1,
                stops: [0, 90, 100]
            }
        },
    };

    var chart = new ApexCharts(document.querySelector("#chart"), options);
    chart.render();

    // Function to fetch live BTC price from CoinGecko
    async function fetchCryptoData() {
        const response = await fetch('https://api.coingecko.com/api/v3/simple/price?ids=bitcoin&vs_currencies=usd');
        const data = await response.json();
        const price = data.bitcoin.usd;
        const currentTime = new Date().getTime();

        // Add new data point and update chart
        options.series[0].data.push({ x: currentTime, y: price });

        // Keep only the latest 30 data points
        if (options.series[0].data.length > 30) {
            options.series[0].data.shift();
        }

        chart.updateSeries([{
            data: options.series[0].data
        }]);
    }

    // Fetch data every 10 seconds
    setInterval(fetchCryptoData, 10000);

    // Initial data fetch
    fetchCryptoData();

    const firstChart = createChart(document.getElementById('firstContainer'));
    const secondChart = createChart(document.getElementById('secondContainer'));
</script>



